import { PayloadAction, createSlice } from "@reduxjs/toolkit";

interface IssuesState {
  value: {
    map(arg0: (item: any, index: any) => JSX.Element): import("react").ReactNode;
    toDo: string[];
    inProgress: string[];
    done: string[];
  };
}

const issuesSlice = createSlice({
  name: "issues",
  initialState: { value: { toDo: [], inProgress: [], done: [] } } as unknown as IssuesState,
  reducers: {
    toDo: (state, action: PayloadAction<string[]>) => {
      state.value.toDo = action.payload;
    },
    inProgress: (state, action: PayloadAction<string[]>) => {
      state.value.inProgress = action.payload;
    },
    done: (state, action: PayloadAction<string[]>) => {
      state.value.done = action.payload;
    },
  },
});

export const { toDo, inProgress, done } = issuesSlice.actions;
export default issuesSlice;
