import { createSlice } from "@reduxjs/toolkit";

const userslice = createSlice({
  name: "issues",
  initialState: {value:{ toDo: "", inProgress: "", done: "" }},
  reducers: {
    login: (state, action) => {
      state.value = action.payload;
    },
  },
});

export const { login } = userslice.actions;
export default userslice;
