import { PayloadAction, createSlice } from "@reduxjs/toolkit";

interface IssuesState {
  toDo: string[];
  inProgress: string[];
  done: string[];
}

const issuesSlice = createSlice({
  name: "issues",
  initialState: { toDo: [], inProgress: [], done: [] } as IssuesState,
  reducers: {
    toDo: (state, action) => {
      state.toDo = action.payload;
    },
    inProgress: (state, action) => {
      state.inProgress = action.payload;
    },
    done: (state, action) => {
      state.done = action.payload;
    },
  },
});

export type RootState = ReturnType<typeof issuesSlice.getState>
export type AppDispatch = typeof issuesSlice.dispatch
export const { toDo, inProgress, done } = issuesSlice.actions;
export default issuesSlice;
