import React, { useState } from 'react';
import styled from '@emotion/styled';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import TaskCard from './TaskCard';
import { Breadcrumb, Button, Input, Layout, Space, theme } from 'antd';
import { Octokit } from '@octokit/rest';
import { useDispatch } from 'react-redux';
import { toDo } from '../redux/issues';
import { v4 as uuidv4 } from 'uuid';

const { Header, Content, Footer } = Layout;

const Container = styled.div`
  display: flex;
`;

const TaskList = styled.div`
  min-height: 100px;
  display: flex;
  flex-direction: column;
  background: #f3f3f3;
  min-width: 341px;
  border-radius: 5px;
  padding: 15px 15px;
  margin-right: 45px;
`;

const TaskColumnStyles = styled.div`
  margin: 8px;
  display: flex;
  width: 100%;
  min-height: 80vh;
`;

const Title = styled.span`
  color: #10957d;
  background: rgba(16, 149, 125, 0.15);
  padding: 2px 10px;
  border-radius: 5px;
  align-self: flex-start;
`;

const Home = () => {

  const [text, setText] = useState('');
  const dispatch = useDispatch();

  const getIssues = async (repoUrl: string) => {
    const urlParts = repoUrl.split('/');
    const owner = urlParts[3];
    const repo = urlParts[4];
    const response = await fetch(`https://api.github.com/repos/${owner}/${repo}/issues?state=all`);
    const issues = await response.json();
    dispatch(toDo([issues.filter((item:any) => item.state === "open" && !!!item.assignee)]))
    return issues;
  }

  const columnsFromBackend = {
    [uuidv4()]: {
      title: 'To-do',
      items: text,
    },
    [uuidv4()]: {
      title: 'In Progress',
      items: [],
    },
    [uuidv4()]: {
      title: 'Done',
      items: [],
    },
  };

  const [columns, setColumns] = useState(columnsFromBackend);

  const onDragEnd = (result:any, columns:any, setColumns:any) => {
    if (!result.destination) return;
    const { source, destination } = result;
    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId];
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems,
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems,
        },
      });
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems,
        },
      });
    }
  };

  const {
    token: { colorBgContainer },
  } = theme.useToken();

  return (
    <Layout>
    <Header style={{ position: 'sticky', top: 0, zIndex: 1, width: '100%' }}>
      <div
        style={{
          float: 'left',
          width: 120,
          height: 31,
          margin: '16px 24px 16px 0',

        }}
      />
    </Header>
    <Content className="site-layout" style={{ padding: '0 50px' }}>
      <Breadcrumb style={{ margin: '16px 0' }}>
        <Breadcrumb.Item>...</Breadcrumb.Item>
        <Breadcrumb.Item>...</Breadcrumb.Item>
      </Breadcrumb>
      <div style={{ padding: 24, minHeight: 380, background: colorBgContainer }}>
      <Space.Compact style={{ width: '100%' }}>
      <Input 
        autoFocus
        type={"text"}
        value={text}
        onChange={(e:any) => {
          setText(e.target.value);
        }}
        defaultValue="Combine input and button" 
      />
      <Button onClick={() => getIssues(text)} type="primary">Load</Button>
    </Space.Compact>
    <br/>
    <br/>
    <div className='drag'>
      <DragDropContext
onDragEnd={(result) => onDragEnd(result, columns, setColumns)}
>
<Container>
  <TaskColumnStyles>
    {Object.entries(columns).map(([columnId, column], index) => {
      return (
        <Droppable key={columnId} droppableId={columnId}>
          {(provided, snapshot) => (
            <TaskList
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              <Title>{column.title}</Title>
              {Array.isArray(column.items) && column.items.map((item: string, index: number) => (
                <TaskCard key={index} id={index} text={item} />
              ))}
              {provided.placeholder}
            </TaskList>
          )}
        </Droppable>
      );
    })}
  </TaskColumnStyles>
</Container>
</DragDropContext>
</div>
      </div>
    </Content>
    <Footer style={{ textAlign: 'center' }}>Ant Design ©2023 Created by Ant UED</Footer>
  </Layout>

  );
};

export default Home;
