import { PayloadAction, createSlice } from "@reduxjs/toolkit";

interface IssuesState {
    map(arg0: (item: any, index: number) => JSX.Element): import("react").ReactNode;
    value: any[];
}

const issuesSlice = createSlice({
  name: "issues",
  initialState: { value: []} as unknown as IssuesState,
  reducers: {
    issues: (state, action: PayloadAction<any>) => {
      state.value = action.payload;
    }
  },
});

export const { issues } = issuesSlice.actions;
export default issuesSlice;
