import React, { useState } from 'react';
import styled from '@emotion/styled';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import { Breadcrumb, Button, Input, Layout, Space, theme } from 'antd';
import { Octokit } from '@octokit/rest';
import { useDispatch } from 'react-redux';
import { done, inProgress, toDo } from '../redux/issues';
import { useSelector } from 'react-redux';
import { useAppSelector, useAppDispatch } from '../redux/hooks'
import { v4 as uuidv4 } from 'uuid';


const { Header, Content, Footer } = Layout;

const Home = () => {
  const [text, setText] = useState('');  

  const getIssues = async (repoUrl: string) => {
    const urlParts = repoUrl.split('/');
    const owner = urlParts[3];
    const repo = urlParts[4];
    const response = await fetch(`https://api.github.com/repos/${owner}/${repo}/issues?state=all`);
    const issues = await response.json();
    dispatch(toDo([issues.filter((item:any) => item.state === "open" && !!!item.assignee)]))
    dispatch(inProgress([issues.filter((item:any) => item.state === "open" && !!item.assignee === true)]))
    dispatch(done([issues.filter((item:any) => item.state === "closed")]))
    console.log(toDoState.toDo)
    console.log(inProgressState.inProgress)
    console.log(doneState.done)
    return issues;
  }
  const dispatch = useDispatch();
  const toDoState = useAppSelector((state) => state.issues.value)
  const inProgressState = useAppSelector((state) => state.issues.value)
  const doneState = useAppSelector((state) => state.issues.value)

  const {
    token: { colorBgContainer },
  } = theme.useToken();

  const [boards, setBoards] = useState([
    {id: 1, title: "ToDo", items: [{id: 1, title: "пойти в магазин"}, {id:2, title: "выкинуть мусор"}]},
    {id: 2, title: "inProgress", items: [{id: 1, title: " попить чай"}, {id:2, title: "погулять с собакой"}]},
    {id: 3, title: "Done", items: [{id: 1, title: "выпить кофе"}, {id:2, title: "поиграть"}]}
  ]);
  console.log()

  const [currentBoard, setCurrentBoard] = useState(null)
  const [currentItem, setCurrentItem] = useState(null)

  const dragOverHandler = (e:any, board:any, item:any) => {
   e.preventDefault()
   if (e.target.className == "item") {
    e.target.style.boxShadow = '0 2px 3px gray'
   }
  }

  const dragLeaveHandler = (e:any, board:any, item:any) => {
    e.target.style.boxShadow = 'none'
  }

  const dragStartHandler = (e:any, board:any, item:any) => {
    setCurrentBoard(currentBoard)
    setCurrentItem(currentItem)
  }

  const dragEndHandler = (e:any, board:any, item:any) => {
    e.target.style.boxShadow = 'none'
  }

  const dropHandler = (e: React.DragEvent, board: any, item: any) => {
    e.preventDefault()
    const currentIndex = currentBoard.items.indexOf(currentItem)
    currentBoard.items.splice(currentIndex, 1)
    const dropIndex = boards.items.indexOf(item)
    board.items.splice(dropIndex + 1, 0, currentItem)
    setBoards(boards.map((b) => {
      if(b.id === board.id) {
        return board;
      }
      if(b.id === currentBoard.id) {
        return currentBoard;
      }
    }))
  }

  return (
    <Layout>
    <Header>

    </Header>
    <Content className="site-layout" style={{ padding: '0 50px' }}>
      <Breadcrumb style={{ margin: '16px 0' }}>
        <Breadcrumb.Item>...</Breadcrumb.Item>
        <Breadcrumb.Item>...</Breadcrumb.Item>
      </Breadcrumb>
      <div style={{ padding: 24, background: colorBgContainer }}>
      <Space.Compact style={{ width: '100%' }}>
      <Input 
        autoFocus
        type={"text"}
        value={text}
        onChange={(e:any) => {
          setText(e.target.value);
        }}
        defaultValue="Combine input and button" 
      />
      <Button onClick={() => getIssues(text)} type="primary">Load</Button>
    </Space.Compact>
    <br/>
    <br/>
      <div className='app'>
        {boards.map((board) => 
        <div className="board">
          <div className="board_title">
            {board.title}
          </div>
          {board.items.map((item) => 
          <div 
          onDragOver={(e) => dragOverHandler(e, board, item)}
          onDragLeave={(e) => dragLeaveHandler(e, board, item)}
          onDragStart={(e) => dragStartHandler(e, board, item)}
          onDragEnd={(e) => dragEndHandler(e, board, item)}
          onDrop={(e) => dropHandler(e, board, item)}
          draggable={true}
          className='item'>
            {item.title}
          </div>
          )}
        </div> 
       )}
      </div>
    </div>
    </Content>
    <Footer style={{ textAlign: 'center' }}>Ant Design ©2023 Created by Ant UED</Footer>
  </Layout>

  );
};

export default Home;

