import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import styled from '@emotion/styled';
import { useDispatch } from 'react-redux';
import { useAppSelector } from '../redux/hooks';
import { done, inProgress, toDo } from '../redux/issues';

const TaskInformation = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  padding: 0 15px;
  min-height: 106px;
  border-radius: 5px;
  max-width: 311px;
  /* background: ${({ isDragging }:any) =>
    isDragging ? 'rgba(255, 59, 59, 0.15)' : 'white'}; */
  background: white;
  margin-top: 15px;

  .secondary-details {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    font-size: 12px;
    font-weight: 400px;
    color: #7d7d7d;
  }
  /* .priority{ */
  /* margin-right: 12px; */
  /* align-self: center;
    svg{
      width: 12px !important;
      height: 12px !important;
      margin-right: 12px; */
  /* margin-top: 2px; */
  /* } */
  /* } */
`;



const TaskCard = ({ item, index }:any) => {

  const dispatch = useDispatch();
  const openState = useAppSelector((state) => state.issues.value)
  const openAsigneeState = useAppSelector((state) => state.issues.value)
  const closedState = useAppSelector((state) => state.issues.value)


  const getIssues = async (repoUrl: string) => {
    const urlParts = repoUrl.split('/');
    const owner = urlParts[3];
    const repo = urlParts[4];
    const response = await fetch(`https://api.github.com/repos/${owner}/${repo}/issues?state=all`);
    const issues = await response.json();
    dispatch(toDo([issues.filter((item:any) => item.state === "open" && !!!item.assignee)]))
    dispatch(inProgress([issues.filter((item:any) => item.state === "open" && !!item.assignee === true)]))
    dispatch(done([issues.filter((item:any) => item.state === "closed")]))
    console.log(openState.toDo)
    console.log(openAsigneeState.inProgress)
    console.log(closedState.done)
    return issues;
  }

  return (
    <Draggable key={item.id} draggableId={item.id} index={index}>
      {(provided:any) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
        >
          <TaskInformation>
            <p>{item.title}</p>
            <div className="secondary-details">
              <p>
                <span>
                  {

                  }
                </span>
              </p>
            </div>
          </TaskInformation>
        </div>
      )}
    </Draggable>
  );
};

export default TaskCard;
