import React, { useState } from 'react';
import { Breadcrumb, Button, Input, Layout, Space, theme } from 'antd';
import { useDispatch } from 'react-redux';
import { done, inProgress, toDo } from '../redux/issues';
import { useAppSelector } from '../redux/hooks'
import Link from 'antd/es/typography/Link';

const { Header, Content, Footer } = Layout;

type ItemType = {
  id: number;
  name: string;
  column: string;
  number?: number
};

type ColumnType = {
  id: string;
  title: string;
};


const Home = () => {
  const [text, setText] = useState<string>('');
  const [owner, setOwner] = useState<string>('');
  const [repo, setRepo] = useState<string>('');
  const [ownerLink, setOwnerLink] = useState<string>('');
  const [repoLink, setRepoLink] = useState<string>('');
  const [items, setItems] = useState<ItemType[]>([]);
  const [comments, setComments] = useState<string>('');

  const columns: ColumnType[] = [
    { id: 'ToDo', title: 'ToDo' },
    { id: 'InProgress', title: 'In Progress' },
    { id: 'Done', title: 'Done' },
  ];

  const initialItems: ItemType[] = [
    { id: 1, name: 'Item 1', column: 'column1' },
    { id: 2, name: 'Item 2', column: 'column1' },
    { id: 3, name: 'Item 3', column: 'column1' },
    { id: 4, name: 'Item 4', column: 'column2' },
    { id: 5, name: 'Item 5', column: 'column2' },
    { id: 6, name: 'Item 6', column: 'column2' },
    { id: 7, name: 'Item 7', column: 'column3' },
    { id: 8, name: 'Item 8', column: 'column3' },
    { id: 9, name: 'Item 9', column: 'column3' },
  ];

  const getIssues = async (repoUrl: string) => {
    setText('');
    const urlParts = repoUrl.split('/');
    const ownerName = urlParts[3];
    const repoName = urlParts[4];
    setOwner(ownerName);
    setRepo(repoName);
    const response = await fetch(`https://api.github.com/repos/${owner}/${repo}/issues?state=all`);
    const issues = await response.json();
    dispatch(toDo([issues.filter((item:any) => item.state === "open" && !!!item.assignee)]))
    dispatch(inProgress([issues.filter((item:any) => item.state === "open" && !!item.assignee === true)]))
    dispatch(done([issues.filter((item:any) => item.state === "closed")]))
    const issuesState = issues.map((item:any, index:number) => {
      return {id: index, title: item.title, number: item.number, column: item.state === "open" && !!!item.assignee? "ToDo": item.state === "open" && !!item.assignee === true? "InProgress": "Done"}
    })
    setItems(issuesState)
    const linkForRepo = issues.
    console.log(issuesState, 'issues')
    return issues;
  }

  const dispatch = useDispatch();
  const [draggedItem, setDraggedItem] = useState<ItemType | null>(null);

  const {
    token: { colorBgContainer },
  } = theme.useToken();

  const handleDragStart = (e: React.DragEvent<HTMLDivElement>, item: ItemType) => {
    setDraggedItem(item);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setDragImage(e.currentTarget, 20, 20);
  };

  const handleDragOver = (e: React.DragEvent<HTMLDivElement>, columnId: string, index: number) => {
    e.preventDefault();
    if (draggedItem === null) {
      return;
    }
    if (draggedItem.column === columnId) {
      const updatedItems = [...items];
      updatedItems.splice(updatedItems.indexOf(draggedItem), 1);
      updatedItems.splice(index, 0, draggedItem);
      setItems(updatedItems);
    } else {
      const updatedItems = items.map((item) =>
        item.id === draggedItem.id ? { ...item, column: columnId } : item
      );
      const columnItems = updatedItems.filter(item => item.column === columnId);
      if (columnItems.length === 0) {
        updatedItems.push({ ...draggedItem, column: columnId });
      }
      setItems(updatedItems);
    }
  };  

const handleDrop = (index: number) => {
  if (draggedItem !== null) {
    const updatedItems = [...items];
    const draggedItemIndex = updatedItems.findIndex(item => item.id === draggedItem.id);
    updatedItems[draggedItemIndex] = { ...draggedItem, column: columns[index].id };
    setItems(updatedItems);
    // Check if the item was dropped into an empty column and add it to the end if it's not already there
    const columnItems = updatedItems.filter(item => item.column === columns[index].id);
    if (columnItems.length === 0) {
      updatedItems.splice(index, 0, { ...draggedItem, column: columns[index].id });
      setItems(updatedItems);
    }
  }
};

  const handleTest = (e: React.DragEvent, index: number, id: number) => {
    console.log(e.target)
  }  


  const handleDragEnd = () => {
    setDraggedItem(null);
  };

  return (
    <Layout>
    <Header>
      <div className='logo'>
        <h1>GitHub Kanban Board</h1>
      </div>
    </Header>
    <Content className="site-layout" style={{ padding: '0 50px' }}>
      <Breadcrumb style={{ margin: '16px 0' }}>
        <Breadcrumb.Item><Link href={``}>{owner}</Link></Breadcrumb.Item>
        <Breadcrumb.Item>{repo}</Breadcrumb.Item>
      </Breadcrumb>
      <div className='layout'>
      <Space.Compact style={{ width: '100%' }}>
      <Input 
        autoFocus
        id='link'
        type={"text"}
        value={text}
        onChange={(e:any) => {
          setText(e.target.value);
        }}
        defaultValue="Combine input and button" 
        placeholder='Enter the link to the repo'
      />
      <Button onClick={() => getIssues(text)} type="primary">Load issues</Button>
    </Space.Compact>
    <br/>
    <br/>
    <div className="kanban-board">
      {columns.map((column, index) => (
        <div key={column.id} className="kanban-column">
          <h2>{column.title}</h2>
          <div
            className="kanban-dropzone"
            onDragOver={(e) => handleDragOver(e, column.id, index)}
            onDrop={(e) => e.preventDefault()}
            onDragEnter={(e) => e.preventDefault()}
          >
            {items
              .filter((item:any) => item.column === column.id)
              .map((item:any, index) => (
                <div
                  key={item.id}
                  className="kanban-item"
                  draggable
                  onDragStart={(e) => handleDragStart(e, item)}
                  onDragOver={(e) => handleTest(e, index, item.id)}
                  onDragEnd={() => setDraggedItem(null)}
                >
                  {item.title}
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
    </div>
    </Content>
    <Footer style={{ textAlign: 'center' }}>Ant Design ©2023 Created by Filatov Danylo</Footer>
  </Layout>

  );
}
export default Home;
